class Solution {
public:
    vector<string> restoreIpAddresses(string s) {
        vector<string>ans;
        int n=s.size();
        string s1,s2,s3,s4,res;
        s1=s2=s3=s4=res="";
        for(int i=1;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    s1=s.substr(0,i);
                    s2=s.substr(i,j-i);
                    s3=s.substr(j,k-j);
                    s4=s.substr(k);
                    if(s4.size()==0 || s3.size()==0 || s2.size()==0 || s1.size()==0) 
                        continue;

                    if(stoll(s1)>255 || (s1[0]=='0' && s1.size()>1)) continue;
                    if(stoll(s2)>255 || (s2[0]=='0' && s2.size()>1)) continue;                        
