class Solution {
    public List<String> restoreIpAddresses(String s) {

        List<String> ans = new ArrayList<String>(); // for returing the ans list
        List<String> ls = new ArrayList<String>(); // for calculating IP address of exactly with 4 dots

        allValidAddress(ans, ls, s);

        return ans;

    }

    public void allValidAddress(List<String> ans, List<String> ls, String s)
    {
        int n = s.length();

        if(ls.size() >= 4 && n != 0) // if ls length is 4 but our s string length is not equal to 0 means to valid IP.
        {
            return; // returning from recurive call
        }

        if(n == 0) // if n == 0 
        {
            if(ls.size() == 4) // if ls list size is 4 so we can say we genrated a valid IP address with 4 dots
            {
                StringBuilder sb = new StringBuilder();

                for(int i = 0; i < ls.size(); i++)
                {
                    if(i == 0)
                    {
                        sb.append(ls.get(i));
                    }
                    else
                    {
                        sb.append("." + ls.get(i));
                    }
                }
                ans.add(sb.toString()); // adding IP address in ans list
            }
            return; // returning from the recursive call
        }

        for(int i = 0; i < 3 && i < n; i++) // every time taking a string less than or equal to 3 size from s string
        {
            String tem = s.substring(0, i + 1); // taking tem string from 0 to i.

            if(tem.length() > 1 && tem.charAt(0) == '0') //cheking leading zeros if tem contains the leading zeors.
            {    
                return; // returning from recursive call
            }

            int x = Integer.parseInt(tem); // converting tem to int

            if(x <= 255) // if x <= 255
            {
                ls.add(tem); // adding tem is ls

                allValidAddress(ans, ls, s.substring(i + 1)); // doing recursive call for next substring from i to n.

                ls.remove(ls.size() - 1); 
        // if any condition becomes true for not a valid IP so during backtracking we have to undo all changes we made
            }
        }
    }
}
