//if digit is zero and the number after previous .(consider "0.0") is also zero then there wont be any further placing of . in this current loop so we break the for loop
                                if(num==0)
                                break;

//else the number was not zero so we can make use of normal backtracking
                                else
                                        temp.pop_back();
                        }

//Normal BackTrack for any other digit
                        else {
                                temp=temp+s[i]+".";
                                 func(ans,temp,i+1,s,c+1);
                                temp.pop_back();
                        }
                }
        }
