            rem = data[pos+1:pos+X]
            ret = (f&l[X]) == 0
            for i in range(X):
                ret &= (f&l[i]) != 0
            for num in rem:
                ret &= (num&l[0]) != 0
                ret &= (num&l[1]) == 0
            return ret

        @cache
        def res(pos = 0):
            ret = False
            if pos == n:
