public:
    bool solve(vector<int> &data, int i,int n){
        if(i == n){
            return true;
        }
        if((data[i]&(1<<7)) == 0){
            return solve(data,i+1,n);
        }
        else if(i<(n-1) && (data[i]&(1<<7)) != 0 && (data[i]&(1<<6)) != 0 && (data[i]&(1<<5)) == 0){
            if((data[i+1]&(1<<7)) != 0 && (data[i+1]&(1<<6)) == 0) return solve(data,i+2,n);
