        else if(i<(n-3) && (data[i]&(1<<7)) != 0 && (data[i]&(1<<6)) != 0 && (data[i]&(1<<5)) != 0 && (data[i]&(1<<4)) != 0 && (data[i]&(1<<3)) == 0){
