		else if((data[i] & 0xF0) == 0xE0) conts = 2;
		else if((data[i] & 0xE0) == 0xC0) conts = 1;
		else if((data[i] & 0x80) == 0x00) conts = 0;
		else return false;
	}
	return !conts;
}
