    //function to check whether an integer is a continuation integer or not.
    bool cont(int x){
        int mask1= (1<<7),mask2=(1<<6);
        if((x&mask1)!=0 && (x&mask2)==0){
            return true;
        }
        else return false;
    }
    // function to calculate character classification (1-4).
    int byte(int x){
        if((x&(1<<7))==0){
            return 1;
        }
        int mask=(1<<8)-1,temp;
        x=(x^mask);
        for(int i=7;i>=0;i--){
            temp=(1<<i);
            if((temp&x)!=0){
                temp=i;
                break;
            }
        }
        if(7-temp>=2 && 7-temp<=4){
            return 7-temp;
        }
        else return -1;
    }
    bool validUtf8(vector<int>& data) {
        int bytes,idx=0,n=data.size(),contInt=0;
        bool shCont=false;
        while(idx<n){
            if(shCont){
