impl Solution {
    pub fn valid_utf8(data: Vec<i32>) -> bool {
        let mut next_bytes = 0;
        for ones in data.iter().map(|&x| (x as u8).leading_ones()) {
            match (next_bytes == 0, ones) {
                (false, 1) => next_bytes -= 1,
                (true, 0 | 2 | 3 | 4) => next_bytes = ones.saturating_sub(1),
                _ => return false,
            }
        }
        next_bytes == 0
    }
}
