  // Returs the expected number of bytes in the UTF8 character.
    int get_num_bytes(int data) {
        if (data >> 7 == 0) { // 0xxxxxxx >> 7 == 0
            return 1;
        }
        if (data >> 5 == 6) { // 110xxxxx >> 5 == 6
            return 2;
        }
        if (data >> 4 == 14) { // 1110xxxx >> 4 == 14
            return 3;
        }
        if (data >> 3 == 30) { // 11110xxx >> 3 == 30
            return 4;
        }
        return 0;
    }

    // Returns whether the following byte sequence is valid.
    bool is_valid_multibyte(int data) {
       return (data >> 6 == 2); // 10xxxxxx >> 6 == 2
    }

    bool validUtf8(vector<int>& data) {
        for (int byte_index = 0; byte_index < data.size(); ++byte_index) {
            auto num_bytes = get_num_bytes(data[byte_index]);
            if (num_bytes == 0) {
                return false;
            }
            if (num_bytes == 1) { 
                // We are not expecting any following bytes for single byte
                // UTF8 character.
                continue;
            }
            if (data.size() < num_bytes + byte_index) {
                // Make sure we have the expected number of bytes in the
                // data vector for this UTF8 character.
                return false;
            }
            // Make sure that all the byte sequences are valid.
            while(--num_bytes > 0) {
                ++byte_index;
                if (!is_valid_multibyte(data[byte_index])) {
                    return false;
                }
            }
        }
        return true;
    }
