class Solution {
public:
// We will iterate through array. Let, we are at data[i]. So,count no. of 1 bits in its MSB position. For this, I have used count1Bits() function
// If no. of 1 bits in leftmost position=0 then it is 1 Byte character (& it should start from zero which it is starting as its leftmost position=0), so simply do i++.
// If no. of 1 bits in leftmost position=1 then it is not valid. Bcoz, leftmost 1's should be zero or >=2 & <4

//Once, we get count of 1's in leftmost position at data[i], let these are 4, so next 3 numbers should start with 10. Now, to check if next 3 numbers are starting with 10 or not, I have called isCorrect() function

    int count1Bits(int n){
        if(((1<<7)&n)==0)
            return 1;
        int x=7, count=0;
        while(x>=0){
            if(((1<<x)&n)>0){
                count++;
                x--;
            }
            else
                return count;
        }
        return count;
    }

    bool isCorrect(int n){
        if(((1<<7)&n)==0)
            return false;
        if(((1<<6)&n)==0)
            return true;
        return false;
    }

    bool validUtf8(vector<int>& data) {
        int sz=data.size();
        int i=0;

        while(i<sz){
            int n=count1Bits(data[i]);
            cout<<n<<" ";

            if(n==1){
                if(((1<<7)&data[i])==0)
                i++;
                else   //if no. of 1 in leftmost position=1, then it is invalid
                return false;
            }

        //since, a character in utf-8 can be from 1 to 4 bytes long. So, if n>4, return false
            else if(n>4)  
            return false;

            else{
                int j=0;

// If no. of 1's in leftmost position=4 then we need to check next 3 numbers. So, that's why I have done n--
                n--;  


                while((i+1)<sz && j<n){
                    if(isCorrect(data[i+1])==false)
                        return false;
                    i++;
                    j++;
                }
                if(j!=n)
                    return false;
                i++;
            }
        }
        return true;
    }
};
