class Solution {
public:
    int find(int val) {
        int res = 0, len = 8;
        while(val and len) {
            if(val&1) res++;
            else res = 0;
            val>>=1, len--;
        }
        if(len) return 0;
        return res;
    }
    bool notPossible(vector<int>& data, int idx, int ones, int n) {
        idx++, ones--;
        while(idx < n and ones and find(data[idx]) == 1) idx++, ones--;
        return ones;

    }
    bool validUtf8(vector<int>& data) {
        int idx = 0, n = data.size();
        while(idx < n) {
            int val = find(data[idx]);
            if(val == 1 or val > 4) return 0;
            if(val) {
                if(notPossible(data, idx, val, n)) return 0;
                idx += val;
            }
            else idx++;
        }
        return 1;
    }
};
