bool validUtf8(const int* data, const int len){
    unsigned char match;
    for(int i = 0; i < len; i += 1)
    {
        if((0x80 & data[i]) == 0x00)
            continue;
        else if((0xE0 & data[i]) == 0xC0)
            match = 1;
        else if((0xF0 & data[i]) == 0xE0)
            match = 2;
        else if((0xF8 & data[i]) == 0xF0)
            match = 3;
        else
            return false;

        if(i + match >= len)
            return 0;
        for(; match > 0; match -= 1)
            if((0xC0 & data[++i]) != 0x80)
                return false;
    }

    return true;
}
