class Solution {
    public boolean validUtf8(int[] data) {

        int n = data.length, byt = 0;
        for( int i=0; i<n; i++ ){

            if( byt==0 ) {
				// to check how many bytes number we currently have
				// when the number is one byte -> directly checking left most bit
                if( ((data[i]>>7)&1)==0 ) continue;
				// to get the number of left most 1's -> n 1 -> leading to (n-1) terms starting with "10"
                else {
                    byt = encoding(data[i]);
					// if byt is 0 , it means number only has 1 left most bit which is not valid
                    if( byt==0 ) return false;
                }
            }
            else{
                int num = data[i];
                num = num>>6;
				// to check whether the number starts with 10 or not 
                if( num!=2 ) return false;
                byt--;
            }
            // the number of terms after the main byte cannot be greater than or equal to 4
            if( byt>=4 ) return false;
        }
        if( byt!=0 ) return false;
        return true;
    }

	// function to return left most 1's of a number
    public int encoding( int num ){
        int pos = 1, res = 0;
        while( num>0 ){
            if( (num&1)==0 ) res = pos;
            pos++;
            num = num>>1;
        }

        return (8-res-1);
    }
}
