var validUtf8 = function(data) {
    let j = 0, count = 0;

    while(j < data.length){
        let ele = data[j].toString(2);
        ele = "00000000".substring(ele.length) + ele;
        if(ele.substring(0,2) === "10")
            return false;

        for(key in myObj){
