        for(int i=0;i<8 and t[i]!='0';i++){
            cnt++;
        }
        return cnt;
    }

    bool validUtf8(vector<int>& data) {
        vector<string> check; 
        for(auto v: data){  //4
            string temp="";
            for(int i=0;i<8;i++){
                if(v&(1<<i)){
                    temp.push_back('1');
                }
                else temp.push_back('0');
            }
            reverse(temp.begin(),temp.end());
            check.push_back(temp);
        }

        // for(auto v:  check) cout<<v<<" ";
        //encoding done of 8-LSB

        int i=0, sz=check.size();
        while(i<sz){
            int cnt = findOnes(check[i]);
            if(cnt>=2 and cnt<=4){ //5
                int j=cnt-1;
                if(i+j>=sz) return false;  //6
                int k=1;
                while(k<=j){//8
                    int ones=findOnes(check[i+k]);
                    if(ones!=1) return false; //7
                    k++;
                }
                i+=cnt;
            }
            else if(cnt!=0) return false;  //2
            else i++; //3
        }

        return true;
    }
