var validUtf8 = function(data) {
    const n = data.length;

    const calculateSetMsbs = (num) => {
        let i = 7;
        let setMsbs = 0;

        while(i >= 0) {
            if(((1 << i) & num) === 0) break;
            setMsbs += 1;
            i -= 1;
        }

        return setMsbs;
    }

    const msbs = [];
    for(let i = 0; i < n; i += 1) {
        const setMsbs = calculateSetMsbs(data[i]);
        msbs.push(setMsbs);
    }

    const dfs = (i = 0) => {
        if(i === n) return true;

        const setMsbs = msbs[i];
        if(setMsbs === 0) {
            return dfs(i + 1);
        }
        if(setMsbs === 2) {
            return i + 1 < n && msbs[i + 1] === 1 && dfs(i + 2);
        }
        if(setMsbs === 3) {
            return i + 2 < n && msbs[i + 1] === 1 && msbs[i + 2] === 1 && dfs(i + 3);
        }
        if(setMsbs === 4) {
            return i + 3 < n && msbs[i + 1] === 1 && msbs[i + 2] === 1 && msbs[i + 3] === 1 && dfs(i + 4);
        }

        return false;
    }

    return dfs();
};
```
