class Solution {
public:
    // return number of bytes need to be proccesed (for example: for 4 bytes return 3), -1 on error;
    int parse_first_byte(int byte) {
        if ((byte >> 7) == 0) { // if mask is 0xxxxxxx
            return 0;
        } else if ((byte >> 5) == 6) { // if mask is 110xxxxx
            return 1;
        } else if ((byte >> 4) == 14) { // if mask is 1110xxxx
            return 2;
        } else if ((byte >> 3) == 30) { // if mask is 11110xxx
            return 3;
        } 
        return -1;
    }

    bool validUtf8(vector<int>& data) {
        int need_to_process{};
        for (int i = 0; i < data.size(); ++i) {
            if (need_to_process == 0) {
                need_to_process = parse_first_byte(data[i]);
                // Check how many bytes we need to process, if there are no available data or error: return false
                if ((need_to_process == -1) || (data.size() - i < need_to_process)) {
                    return false;
                }
            } else {
                // if we check n-bytes and it is doesn't match 10xxxxxx mask, return error
                if ((data[i] >> 6) != 2) {
                    return false;
                } else {
                    --need_to_process;
                }
            }
        }
        return true;
    }
};
