```c++
// the limit given by the problem description
const int MAX_BYTES_UTF8_CHARACTER = 4;

class Solution {
public:
    bool validUtf8(vector<int> &data) {
        const int n = data.size();

        int i = 0;
        while (i < n) {
            // gets the utf-8 header
            // clz: count leading zeros, using as count leading ones with bitwise negate
            int header = __builtin_clz(~(data[i] << 24));
            // continuation header should not appear here
            if (header == 1)
                return false;

            // make header 0 (ascii characters) to be 1 byte
            int bytes = std::max(1, header);
            if (bytes > MAX_BYTES_UTF8_CHARACTER || i + bytes > n)
                return false;

            // ensure there are (bytes - 1) continuation bytes
            for (int j = 1; j < bytes; ++j)
                if ((data[i + j] >> 6) != 0b10)
                    return false;

            i += bytes;
        }
        return i == n;
    }
};
```

