        string decToBinary(int n)
        {
            string s;
            for (int i = 7; i >= 0; i--)
            {
                int k = n >> i;
                if (k & 1)
                    s += "1";
                else
                    s += "0";
            }
            return s;
        }

    bool validUtf8(vector<int> &data)
    {
        vector<string> vs;
        for (int i = 0; i < data.size(); i++)
        {
            string str = decToBinary(data[i]);
            vs.push_back(str);
           	// cout<<str<<" ";
        }
        for (int i = 0; i < vs.size(); i++)       //iterate in vector and check the given condition
        {
            string s = vs[i];
            cout << s << "\n";
            int cnt = 0;
            if (s[0] == '0')
            {
                continue;            // if start index 0 is 0 , so continue.
            }
            for (int j = 1; j < s.size(); j++)
            {
                if (s[j] == '1')
                {
                    cnt++;      // counting 1 
                    if (cnt >= 4)
                    {
                        return false; 
                    }
                }
                else
                {
                    break;
                }
            }

            if (cnt > 0)   //if count is greater the 1 then check next count elements in vecotor otherwise false
            {
                int j = i + 1;
                if (j > vs.size() - 1)        // if element is not present so return false;
                {
                    return false;
