class Solution {
    func validUtf8(_ data: [Int]) -> Bool {
        var i = 0
        while i < data.count {
            var num = data[i]
            i += 1
            guard num & 0x80 != 0 else { continue }
            var charactersLeft = -1 
            while num & 0x80 != 0 {
