class Solution:
    def validUtf8(self, data: List[int]) -> bool:

        # Convert to strings of bins
        data = [str(bin(x))[2:] for x in data]

        # Loop until we've popped all from data
        while True:

            # Get the prefix (tells us how many bytes we expect)
            try:
                prefix = data.pop(0)
            except:
                break

            # If less than eight, we don't expect any more, we can get next prefix
            if len(prefix) < 8:
                continue

            # Otherwise read the number of 1's on the prefix
            num_tens = len(prefix.split('0')[0])-1

            # If greater than three, it's an error
            if num_tens > 3:
                return False
            # If less than zero, it's also an error
            elif num_tens < 0:                
                continue            
            # If zero exaclty, also an error
            elif num_tens == 0:
                return False
            # Else, num_tens tells us how many bytes we expect to read
            else:
                # We pop the num bytes we expect
                for _ in range(num_tens):

                    # If we fail to pop, its invalid string
                    try:
                        char = data.pop(0)
                    except:
                        return False

                    # Make sure its prefixed by 10 and at least eight char, else invalid
                    if char[:2] == '10' and len(char) == 8:
                        num_tens -= 1
                    else:
                        return False

                # If we didn't reach exactly num_tens bytes, it's error
                if num_tens != 0:
                    return False

        # If we made it this far, its valid
        return True
