class Solution {
public:
    bool validUtf8(vector<int>& data) {
        int n = data.size();
        for (int i = 0; i < n; ++i) {
            int firstByte = data[i] & 0x000000FF;
            if (firstByte >> 7 == 0) {
                continue;
            } else if (firstByte >> 5 == 0x06) {
                if (i >= n - 1 || (data[++i] & 0x000000C0) != 0x80) {
                    return false;
                }
            } else if (firstByte >> 4 == 0x0E) {
                if (i >= n - 2 || (data[++i] & 0x000000C0) != 0x80 || (data[++i] & 0x000000C0) != 0x80) {
                    return false;
