public boolean validUtf8(int[] data) {
	if(data == null || data.length == 0)
		return true;

	int i = 0;
	while(i < data.length) {
		// check is most significant bit is 0
		if((data[i] & (1 << 7)) == 0) {
			i++;
			continue;
		}

		// count the no of ones for bytes
		int j = 8;
		while(--j >= 1 && (data[i] & (1 << j)) >= 1) {}

		// calcs the end of bytes
		int end = i + (8 - (j + 1) - 1);

		// returns false if numbytes = 1 or >= 5 or array doesn't have enough bytes
		// checks if the next bit is 0 ot not
		if((data[i] & (1 << j)) >= 1 || end >= data.length || end == i || (end - i + 1) >= 5)  {
			return false;
		}

		// checks if all the bytes starts with 10 or not
		for(int k = i + 1; k <= end; k++) {
			if(((data[k] & (1 << 7)) == 0) || ((data[k] & (1 << 6)) == 1))
				return false;
		}

		// updates the i to begin with integer after end
		i = end + 1;
	}
	return true;
}

