impl Solution {
    pub fn valid_utf8(data: Vec<i32>) -> bool {
        let n: usize = data.len();
        let mut i: usize = 0;

        while i < n {
            let byte: i32 = data[i];
            let offset: usize = if byte & 0x80 == 0x0 {
                    0
                } else if byte & 0xe0 == 0xc0 {
                    1
                } else if byte & 0xf0 == 0xe0 {
                    2
                } else if byte & 0xf8 == 0xf0 {
                    3
                } else {
                    return false;
                };

            if offset == 0 {
                i += 1;
                continue;
            }

            let mut j: usize = 1;

            while i + j < n && j <= offset {
                match data[i + j] & 0xc0 == 0x80 {
                    true => j += 1,
                    false => return false,
                }
            }

            match j == offset + 1 {
                true => i += offset + 1,
                false => return false,
            }
        }
        true
    }
}
```

