    bool validUtf8(vector<int>& data) {
        int cur=0,sz=data.size();
        int cntBytes=0;
        int multiByteStartIndex=0;
        while (cur<sz){
            if ((data[cur] & 1<<7)==0) {
                cur++;
            }
            else {
                cntBytes =0;
                while (cntBytes < 4 && (data[cur] & (1<<(7-cntBytes-1))) >0) {
