                multiByteStartIndex = cur+1;

                while (multiByteStartIndex<sz && cntBytes>0 && (data[multiByteStartIndex]>>6) ==2) {
                    cntBytes--;
                    multiByteStartIndex++;

                }
                if (cntBytes>0) return false;
                cur =multiByteStartIndex;
            }
        }
        return true;
    }
};
