# Code
```
class Solution {
public:
    bool validUtf8(vector<int>& data) {
        auto f = [](int a, int i) {
            return a >> i == (0xff >> i) - 1;
        };

        int cnt = 0;
        for (int a : data)
            if (!cnt) {
                if (f(a, 5))
                    cnt = 1;
                else if (f(a, 4))
                    cnt = 2;
                else if (f(a, 3))
                    cnt = 3;
                else if (!f(a, 7))
                    return false;
            }
            else {
                if (!f(a, 6))
                    return false;

                cnt--;
            }

        return !cnt;
    }
};
```

