class BIT:
    def __init__(self, n):
        self.n = n + 1
        self.sums = [0] * self.n

    def update(self, i, delta):
        while i < self.n:
            self.sums[i] += delta
            i += i & (-i)

    def query(self, i):
        res = 0
        while i > 0:
            res += self.sums[i]
            i -= i & (-i)
        return res

def reversePairs(self, nums):
    """
    :type nums: List[int]
    :rtype: int
    """
    # BIT O(nlogn)
    new_nums = nums + [x * 2 for x in nums]
    sorted_set = sorted(list(set(new_nums)))
    tree = self.BIT(len(sorted_set))
    res = 0
    ranks = {}
    for i, n in enumerate(sorted_set):
        ranks[n] = i + 1

    for n in nums[::-1]:
        res += tree.query(ranks[n] - 1)
        tree.update(ranks[n * 2], 1)

    return res

