class Node {
        int val, lt; 
        Node left, right;
        Node(int val) {
            this.val=val;
            lt=0;
        }
    }
    private int search(Node p, double num) {
        int cnt=0;
        while(p!=null) {
            if(num<p.val) p=p.left;
            else {
                cnt+=p.lt+(num>p.val?1:0);
                p=p.right;
            }
        }
        return cnt;
    }
    private Node insert(Node root, int num) {
        if(root==null) {
            return new Node(num);
        }
        if(num<root.val) {
            root.lt++;
            root.left=insert(root.left,num);
        } else {
            root.right=insert(root.right,num);
        }
        return root;
    }
    public int reversePairs(int[] nums) {
        int cnt=0;
        Node bst=null;
        for(int i=nums.length-1;i>=0;i--) {
            cnt+=search(bst,nums[i]/2.0);
            bst=insert(bst,nums[i]);
        }          
        return cnt;
    }
