        if(l>=r) return 0;
        int mid=(l+r)/2, count=mergeSort(l,mid,nums)+mergeSort(mid+1,r,nums);
        for(int i=l,j=mid+1;i<=mid;i++) {
            while(j<=r&&nums[i]/2.0>nums[j]) j++;
            count+=j-mid-1;
