public int reversePairs4(int[] A) {
        return reversePairsSub(A, 0, A.length - 1);
    }

    private int reversePairsSub(int[] A, int left, int r) {
        if (left >= r) return 0;

        int m = left + ((r - left) >> 1);
        int res = reversePairsSub(A, left, m) + reversePairsSub(A, m + 1, r);

        int i = left, j = m + 1, k = 0, p = m + 1;
        int[] merge = new int[r - left + 1];

        while (i <= m) {
            while (p <= r && A[i] * 1.0 / 2 > A[p]) p++;// avoid overflow, or A[i] > 2 L * A[p]
            res += p - (m + 1);

            while (j <= r && A[j] < A[i]) merge[k++] = A[j++];
            merge[k++] = A[i++];
        }

        //while (j <= r) merge[k++] = A[j++];

        System.arraycopy(merge, 0, A, left, j - left);

        return res;
    }
