public int reversePairs2(int[] A) {
        int n = A.length;
        if (A == null || n <= 1) return 0;
        int[] BIT = new int[n + 1];
        int[] copy = A.clone();
        Arrays.sort(copy);
        int res = 0;

        for (int i = n - 1; i >= 0; i--) {
            res += search(BIT, index(copy, 1.0 * A[i] / 2) - 1);//going down 
            insert(BIT, index(copy, A[i]));// going up
        } 
        return res;
    }

    private int search(int[] BIT, int i) {
        int cnt = 0;
        while (i > 0) {
            cnt += BIT[i];
            i -= (i & -i);
        }
        return cnt;
    }

    private void insert(int[] BIT, int i){
        while (i < BIT.length) {
            BIT[i]++;
            i += (i & -i);
        }
    }          

    //first position in BIT that has A[i] >= val
    private int index(int[] A, double val) {
        int left = 0, r = A.length;
        while (left < r) {
        	int m = left + ((r - left) >> 1);
        	if (A[m] >= val) {
        	    r = m;
        	} else {
        	    left = m + 1;//A[m] < val, so left must be the position 
        	}
        }
        return left + 1;
    } 
