        int j=mid+1;
        // Additional for loop to count number of reversePairs
        for(int i=l; i<=mid; i++){
            while(j<=r && arr[i] > 2*(long long)arr[j]){
                j++;
            }
            cnt+=j-(mid+1);
        }
        vector<int> tmp;
        int left=l,right=mid+1;
        while(left<=mid && right<=r) {
            if(arr[left]<=arr[right]) {
                tmp.push_back(arr[left++]);
