        if (lo == hi) {
            int[] bres = new int[1];
            bres[0] = nums[lo];
            return bres;
        }
        int mid = (lo + hi) / 2;
        int[] lsh = mergeSort(nums, lo, mid);
        int[] rsh = mergeSort(nums, mid + 1, hi);
        return merge(lsh, rsh);
