class Solution {
public:
    vector<pair<int,int>> A;
    long long tree[20*10001],huy,ans=0,n;
    long long BinarySearch(int low,int high,long long  target)
    {
        int mid;
        if(A[high].first<=target) return -1;
        while(low<=high)
        {
            mid=(low+high)/2;
            if(A[mid].first>target&&(mid==0||A[mid-1].first<=target))
            {
                return mid;
            }
            if(A[mid].first>target) high=mid-1;
            if(A[mid].first<=target) low=mid+1;
        }
        return -1;
    }
    void Update(int id,int l,int r,int x)
    {
        if(l>x||r<x) return;
        if(l==r)
        {
            tree[id]++;
            return;
        }
        int mid=(l+r)/2;
        Update(id*2,l,mid,x);
        Update(id*2+1,mid+1,r,x);
        tree[id]=tree[id*2]+tree[id*2+1];
    }
    int Query(int id,int l,int r,int x,int y)
    {
        if(l>y||r<x) return 0;
        if(l>=x&&r<=y) return tree[id];
        int mid=(l+r)/2;
        return Query(id*2,l,mid,x,y)+Query(id*2+1,mid+1,r,x,y);
    }
    int reversePairs(vector<int>& nums) {
        vector<int> picaro(nums.size());
        for(int i=0;i<=nums.size()-1;i++)
            A.push_back(make_pair(nums[i],i));
        sort(A.begin(),A.end());
        for(int i=0;i<=A.size()-1;i++)
            picaro[A[i].second]=i;
        for(int i=0;i<=nums.size()-1;i++)
        {
            if(i==0) Update(1,0,A.size()-1,picaro[i]);
            else
            {
                n=nums[i];
                n=n*2;
                huy=BinarySearch(0,A.size()-1,n);
                if(huy!=-1)
                {
                    ans=ans+Query(1,0,A.size()-1,huy,A.size()-1);
                }
                Update(1,0,A.size()-1,picaro[i]);
            }
        }
        return ans;
    }
};
