#define ll long long
class Solution {
public:
    int ans=0;
    void func(vector<ll> & vec,ll l,ll r)
    {
        ios::sync_with_stdio(false);
        cin.tie(nullptr);
        cout.tie(nullptr);
        if(l==r)
            return;
        ll m=(l+r)/2;
        func(vec,l,m);
        func(vec,m+1,r);
        for(int i=m+1;i<=r;i++)
        {
            int j = upper_bound(vec.begin()+l,vec.begin()+m+1,1LL*2*vec[i])-vec.begin();
            if(j<=m)
                ans += m-j+1;
        }
        int i=l,j=m+1;
        vector<ll> f;
        while(i<=m && j<=r)
        {
            if(vec[i]>vec[j])
                f.push_back(vec[j]),j++;
            else
                f.push_back(vec[i]),i++;
        }
        while(i<=m)
            f.push_back(vec[i++]);
        while(j<=r)
            f.push_back(vec[j++]);
        for(i=l,m=0;i<=r;i++,m++)
            vec[i]=f[m];
        return ;
    }
    int reversePairs(vector<int>& nums) {
        vector<ll> vec(nums.begin(),nums.end());
        func(vec,0,nums.size()-1);
        return ans;
    }
}
