class Solution {
public:
    int countAndSort(vector<int> &nums, int l, int r) {
        if(l>=r) return 0;
        int m = (l+r)/2;
        int ans = countAndSort(nums, l, m) + countAndSort(nums, m+1, r);
        int i=l, j=m+1;
        while(i<=m && j<=r) {
            while(i<=m && nums[i] <= 2*(long long)nums[j]) i++;
            ans += (m-i+1);
            j++;
        }
        inplace_merge(nums.begin()+l, nums.begin()+m+1, nums.begin()+r+1);
        return ans;
    }

    int reversePairs(vector<int>& nums) {
        return countAndSort(nums, 0, (int)nums.size()-1);
    }
};
