class Solution {
    public int reversePairs(int[] nums) {
        return mergesort_and_count(nums,0,nums.length-1);
    }
    int mergesort_and_count(int[] nums,int start,int end){
        if(start<end){
            int mid=(start+end)/2;
            int count=mergesort_and_count(nums,start,mid)+mergesort_and_count(nums,mid+1,end);

            int j=mid+1;
            for(int i=start;i<=mid;i++){
                while(j<=end && (long)nums[i]>(long)nums[j]*2){
                    j++;
                }
                count+=j-(mid+1);
            }

            merge(nums,start,mid,end);
            return count;
        }else{
            return 0;
        }


    }
    void merge(int[] nums,int start,int mid,int end){
        int n1=(mid-start+1);
        int n2=(end-mid);
        int[] L=new int[n1];
        int[] R=new int[n2];

        for(int i=0;i<n1;i++){
            L[i]=nums[start+i];
        }

        for(int j=0;j<n2;j++){
            R[j]=nums[mid+1+j];
