class TreeNode:
    def __init__(self, minv, maxv):
        self.minv = minv
        self.maxv = maxv
        self.left = self.right = None
        self.count = 0

    def add(self, n):
        self.count += 1
        if self.minv == self.maxv:
            return

        mid = (self.minv + self.maxv) // 2
        if not self.left:
            self.left = TreeNode(self.minv, mid)
        if not self.right:
            self.right = TreeNode(mid + 1, self.maxv)
        if n <= mid:
            self.left.add(n)
        else:
            self.right.add(n)

    def countGreaterThan(self, n):
        mid = (self.minv + self.maxv) // 2
        if n > mid:
            return self.right.countGreaterThan(n) if self.right else 0
        else:
            return (self.right.count if self.right else 0) + (self.left.countGreaterThan(n) if self.left else 0)

class Solution:
    def reversePairs(self, nums: List[int]) -> int:
        if not nums: return 0

        cnt = 0
        root = TreeNode(min(nums) - 1, max(nums))
        for n in nums:
            cnt += root.countGreaterThan(n * 2)
            root.add(n)
        return cnt
