```
class Solution {
public:
    int inversions;
    void merge(vector<int>& nums, int left, int mid, int right) {
        int m = mid-left+1;
        int n = right - mid;
        vector<int> l(m), r(n);

        for(int i=0; i<m; i++) l[i] = nums[left+i];
        for(int i=0; i<n; i++) r[i] = nums[mid+i+1];

        long long p;
        int i = 0, j = 0, k = left;
		// in this loop we will check our condition nums[i] > 2*nums[j]
        while(i<m and j<n) {
            p = (long long)r[j]*2;
            if(l[i]>p) {
                inversions += m-i;
                j++;
            } else {
                i++;
            }
        }

        i = 0, j = 0;
        // here in this loop we will actually merge the array
        while(i<m and j<n) {
            if(l[i]>r[j]) {
                nums[k++] = r[j++];
            } else {
                nums[k++] = l[i++];
            }
        }

        while(i<m) nums[k++] = l[i++];
        while(j<n) nums[k++] = r[j++];
    }
    void util(vector<int>& nums, int left, int right) {
        if(left>=right) return;

        int mid = left + (right-left)/2;

        util(nums, left, mid);
        util(nums, mid+1, right);
        merge(nums, left, mid, right);
    }
    int reversePairs(vector<int>& nums) {
        int n = nums.size();
        inversions = 0;
        util(nums, 0, n-1);

        return inversions;
    }
};
