class Solution {
public:

    int inverse(vector<int>& nums, int s, int e, int m)
    {
        int c=0;
        vector<int> temp;
        int i=s,j=m+1;
        while(i<=m and j<=e)
        {
            long long a=nums[i];
            long long b= nums[j];
            b*=2;
