class Solution {
    int count = 0;
    public void merge(int[] arr, int l, int m, int h){
        int lSize = m-l+1;
        int rSize = h-m;
        int[] left = new int[lSize];
        int[] right = new int[rSize];
        for(int i=0;i<lSize;i++){
            left[i] = arr[l+i];
        }
        for(int i=0;i<rSize;i++){
            right[i] = arr[m+1+i];
        }
        int i = 0;
        int j = 0;
        while(i<lSize && j<rSize){
            long lN = (long)left[i];
            long rN = (long)2*right[j];
            if(lN > rN){
                count+=(lSize-i);
                j++;
            }else{
                i++;
            }
        }
        int k = l;
        i = 0;
        j = 0;
        while(i<lSize && j<rSize){
            if(left[i]<=right[j]){
                arr[k++] = left[i++];
            }else{
                arr[k++] = right[j++];
            }

        }
        while(i<lSize){
            arr[k++] = left[i++];
        }
        while(j<rSize){
            arr[k++] = right[j++];
        }
    }
    public void mergeSortUtil(int[] nums, int low, int high){
        if(low<high){
            int mid = low+(high-low)/2;
            mergeSortUtil(nums, low, mid);
            mergeSortUtil(nums, mid+1, high);
            merge(nums, low, mid, high);
        }
    }
    public int reversePairs(int[] nums) {
        mergeSortUtil(nums, 0, nums.length-1);
        return count;
    }
}
