    i = left;
    j = mid;
    k = 0;
    while(i < mid && j <= right) {
        if(nums[i] > nums[j]) {
            temp[k] = nums[j];
            j++;
            k++;
        } else {
            temp[k] = nums[i];
            i++;
            k++;
        }
    }
    while(i < mid) {
        temp[k] = nums[i];
        i++;
        k++;
    }
    while(j <= right) {
        temp[k] = nums[j];
        j++;
        k++;
    }
    k = 0;
    while(left <= right ) {
        nums[left] = temp[k];
        k++;
        left++;
    }
    return pairCount;
}

function mergeSort(nums: number[],left: number,right: number): number {
    let pairCount = 0;
    if(left < right) {
        let mid = Math.floor((right + left)/2)
        pairCount = mergeSort(nums,left,mid);
        pairCount += mergeSort(nums,mid+1,right);
        pairCount += merge(nums,left,mid+1,right);
    }
    return pairCount;
