    private int Merge(int[] nums, int left, int mid, int right)
    {
        int count = 0;
        int[] sortedArray = new int[right - left + 1];
        int leftPartP = left;
        int rightPartP = mid + 1;

        while(leftPartP <= mid && rightPartP <= right)
