    void merge(vector<int>& left, vector<int>& right, vector<int>& nums) {
        int i = 0, j = 0, k = 0;

        for (i = 0; i < left.size(); i++) {            
            while (j < right.size() && left[i] > right[j]*2LL)
                j++;
            count += j;
