        i = 0, j = 0;        
        while (i < left.size() && j < right.size()) {
            if (left[i] < right[j])
                nums[k++] = left[i++];
            else 
                nums[k++] = right[j++];
        }

        while (i < left.size())
            nums[k++] = left[i++];

        while (j < right.size())
            nums[k++] = right[j++];
    }

    void mergeSort(vector<int>& nums) {
        if (nums.size() < 2)
            return;

        int mid = nums.size() / 2;
        vector <int> left;
