        for (int i = 0; i < mid; i++)
            left.push_back(nums[i]);

        for (int i = mid; i < nums.size(); i++)
            right.push_back(nums[i]);

        mergeSort(left);
        mergeSort(right);        

        merge(left, right, nums);
    }

