    int count;
    void checkCount(vector<int>& nums, int start, int mid, int end) {
        int l = start, r = mid + 1;
        while (l <= mid && r <= end){
            if ((long) nums[l] > (long) 2 * nums[r]){
                count += (mid - l + 1);
                r++;
            }else {
                l++;
            }
        }
        int a[end - start + 1], t = 0;
        l = start, r = mid + 1;
        while (l <= mid and r <= end) {
            if (l <= mid and nums[l] < nums[r]) {
                a[t++] = nums[l++];
            } else {
                a[t++] = nums[r++];
            }
