public class Solution {
    public int reversePairs(int[] nums) {
        return mergeSort(nums, 0, nums.length-1);
    }

    private int mergeSort(int[] nums, int start, int end){
        // next three lines are to split the array into half (each recursion until we get start >= end)
        if(start >= end) return 0; 
        int mid = start + (end - start)/2; 
        int cnt = mergeSort(nums, start, mid) + mergeSort(nums, mid+1, end); 

        // 'i' needs to be traversed for left half of array and every 'j' (index of right half) will be compared with each 'i'
        for(int i = start, j = mid+1; i <= mid; i++){
            while(j <= end && nums[i]/2.0 > nums[j]) j++; 
            cnt += j-(mid+1); // since j starts from mid + 1 so we need to remove it from 'j' to get actual count
        }
        Arrays.sort(nums, start, end + 1); 
        return cnt; 
    }

